        while n % i == 0:
            factors.append(i)
            n //= i

    # If n is still greater than 2, it must be prime
    if n > 2:
        factors.append(n)

    return factors

def getUniquePrimeFactorsWithCount(n):
    if not isinstance(n, int) or n < 0:
        return [[], []]
    if n == 0:
        return [[], []]
    if n == 1:
        return [[1], [1]]
    if n == 2:
        return [[2], [1]]

    factors = getAllPrimeFactors(n)
    from collections import Counter
    count = Counter(factors)
    unique_factors = list(count.keys())
    counts = list(count.values())

    return [unique_factors, counts]

def getUniquePrimeFactorsWithProducts(n):
    if not isinstance(n, int) or n < 0:
        return []
    if n == 0:
        return []
    if n == 1:
        return [1]
    if n == 2:
        return [2]

    unique_factors, counts = getUniquePrimeFactorsWithCount(n)
    products = [factor ** count for factor, count in zip(unique_factors, counts)]
    return products

# Input and Output
n = int(input())
print(getAllPrimeFactors(n))
print(getUniquePrimeFactorsWithCount(n))
print(getUniquePrimeFactorsWithProducts(n))

